-- 67d7842dbbe25473c3c32b93c0da8047785f30d78e8a024de1b57352245f9689
library IEEE;
use IEEE.STD_LOGIC_1164.all;
use IEEE.NUMERIC_STD.all;

entity pointpillars_cnn_gmem_m_axi_load is
    generic (
        C_TARGET_ADDR         : INTEGER := 16#00000000#;
        NUM_READ_OUTSTANDING  : INTEGER := 2;
        MAX_READ_BURST_LENGTH : INTEGER := 16;
        BUS_ADDR_WIDTH        : INTEGER := 32;
        BUS_DATA_WIDTH        : INTEGER := 32;
        BYTE_ADDR_MODE        : INTEGER := 0;
        USER_DW               : INTEGER := 16;
        USER_AW               : INTEGER := 32;
        USER_LEN_WIDTH        : INTEGER := 32;
        USER_MAXREQS          : INTEGER := 16;
        USER_RFIFONUM_WIDTH   : INTEGER := 6;
        BUFFER_IMPL           : STRING  := "auto");
    port (
        ACLK                  : in  STD_LOGIC;
        ARESET                : in  STD_LOGIC;
        ACLK_EN               : in  STD_LOGIC;
        out_AXI_ARADDR        : out UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
        out_AXI_ARLEN         : out UNSIGNED(USER_LEN_WIDTH-1 downto 0);
        out_AXI_ARVALID       : out STD_LOGIC;
        in_AXI_ARREADY        : in  STD_LOGIC;
        in_AXI_RDATA          : in  UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
        in_AXI_RLAST          : in  UNSIGNED(1 downto 0);
        in_AXI_RVALID         : in  STD_LOGIC;
        out_AXI_RREADY        : out STD_LOGIC;
        out_BURST_RREADY      : out STD_LOGIC;
        in_HLS_ARADDR         : in  UNSIGNED(USER_AW-1 downto 0);
        in_HLS_ARLEN          : in  UNSIGNED(USER_LEN_WIDTH-1 downto 0);
        in_HLS_ARVALID        : in  STD_LOGIC;
        out_HLS_ARREADY       : out STD_LOGIC;
        out_HLS_RDATA         : out UNSIGNED(USER_DW-1 downto 0);
        out_HLS_RVALID        : out STD_LOGIC;
        in_HLS_RREADY         : in  STD_LOGIC;
        out_HLS_RFIFONUM      : out UNSIGNED(USER_RFIFONUM_WIDTH-1 downto 0));
end entity pointpillars_cnn_gmem_m_axi_load;

architecture behave of pointpillars_cnn_gmem_m_axi_load is
    ------------------------Task and function--------------
    function calc_data_width (x : INTEGER) return INTEGER is
        variable y : INTEGER;
    begin
        y := 8;
        while y < x loop
            y := y * 2;
        end loop;
        return y;
    end function calc_data_width;

    function log2 (x : INTEGER) return INTEGER is
        variable n, m : INTEGER;
    begin
        n := 0;
        m := 1;
        while m < x loop
            n := n + 1;
            m := m * 2;
        end loop;
        return n;
    end function log2;
    ------------------------Parameter----------------------
    constant USER_DATA_WIDTH  : INTEGER := calc_data_width(USER_DW);
    constant USER_DATA_ALIGN  : INTEGER := USER_DATA_WIDTH;
    constant USER_ADDR_ALIGN  : INTEGER := log2(USER_DATA_WIDTH/8);
    constant RREQ_PACK_WIDTH  : INTEGER := USER_AW + USER_LEN_WIDTH;
    constant BUS_ADDR_ALIGN   : INTEGER := log2(BUS_DATA_WIDTH/8);
    -- rdata buffer size 
    constant RBUFF_DEPTH      : INTEGER := MAX_READ_BURST_LENGTH * NUM_READ_OUTSTANDING;
    constant PREFERRED_IMPL   : STRING  := "shiftreg" when (RBUFF_DEPTH <= 32) else BUFFER_IMPL;
    constant TARGET_ADDR      : INTEGER := (C_TARGET_ADDR/(2**USER_ADDR_ALIGN))*(2**USER_ADDR_ALIGN);
    ------------------------Local signal-------------------
    signal next_rreq        : STD_LOGIC;
    signal ready_for_rreq   : STD_LOGIC;
    signal rreq_ready       : STD_LOGIC;
    signal rreq_valid       : STD_LOGIC;
    signal valid_length     : STD_LOGIC;
    
    signal rreq_addr        : UNSIGNED(USER_AW-1 downto 0);
    signal rreq_len         : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal rreq_addr_byte   : UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
    signal rreq_len_byte    : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal tmp_addr         : UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
    signal tmp_len          : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal tmp_valid        : STD_LOGIC;

    signal local_AXI_RVALID : STD_LOGIC;
    signal local_AXI_RREADY : STD_LOGIC;
    signal local_AXI_RDATA  : UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
    signal local_AXI_RLAST  : UNSIGNED(1 downto 0);
    signal in_AXI_RDATA_PACK  : UNSIGNED(BUS_DATA_WIDTH+1 downto 0);
    signal out_AXI_RDATA_PACK : UNSIGNED(BUS_DATA_WIDTH+1 downto 0);
    signal local_AXI_RFIFONUM : UNSIGNED(log2(RBUFF_DEPTH) downto 0);
    signal ready_for_outstanding : STD_LOGIC; 

    signal in_rreq_pack     : UNSIGNED(RREQ_PACK_WIDTH-1 downto 0);
    signal out_rreq_pack    : UNSIGNED(RREQ_PACK_WIDTH-1 downto 0);
    -- regslice io ?  no 
    -- enable regslice on R channel  no 

    component pointpillars_cnn_gmem_m_axi_fifo is
        generic (
            MEM_STYLE         : STRING  := "shiftreg";
            DATA_WIDTH        : INTEGER := 8;
            ADDR_WIDTH        : INTEGER := 4;
            DEPTH             : INTEGER := 16);
        port (
            clk               : in  STD_LOGIC;
            reset             : in  STD_LOGIC;
            clk_en            : in  STD_LOGIC;
            if_full_n         : out STD_LOGIC;
            if_write          : in  STD_LOGIC;
            if_din            : in  UNSIGNED(DATA_WIDTH-1 downto 0);
            if_empty_n        : out STD_LOGIC;
            if_read           : in  STD_LOGIC;
            if_dout           : out UNSIGNED(DATA_WIDTH-1 downto 0);
            if_num_data_valid : out UNSIGNED(ADDR_WIDTH downto 0));
    end component pointpillars_cnn_gmem_m_axi_fifo;

    component pointpillars_cnn_gmem_m_axi_reg_slice is
        generic (
            DATA_WIDTH  : INTEGER := 8);
        port (
            clk         : in  STD_LOGIC;
            reset       : in  STD_LOGIC;
            s_data      : in  UNSIGNED(DATA_WIDTH-1 downto 0);
            s_valid     : in  STD_LOGIC;
            s_ready     : out STD_LOGIC;
            m_data      : out UNSIGNED(DATA_WIDTH-1 downto 0);
            m_valid     : out STD_LOGIC;
            m_ready     : in  STD_LOGIC);
    end component pointpillars_cnn_gmem_m_axi_reg_slice;

begin

    -- Instantiation
    
    fifo_rreq : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        DATA_WIDTH        => RREQ_PACK_WIDTH,
        ADDR_WIDTH        => log2(USER_MAXREQS),
        DEPTH             => USER_MAXREQS)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => out_HLS_ARREADY,
        if_write          => in_HLS_ARVALID,
        if_din            => in_rreq_pack,
        if_empty_n        => rreq_valid,
        if_read           => next_rreq,
        if_dout           => out_rreq_pack,
        if_num_data_valid => open);

    -- ===================================================================
    -- start of ARADDR PREPROCESSOR
    in_rreq_pack    <= in_HLS_ARLEN & in_HLS_ARADDR;
    rreq_len        <= out_rreq_pack(USER_AW + USER_LEN_WIDTH-1 downto USER_AW);
    rreq_addr       <= out_rreq_pack(USER_AW - 1 downto 0);

    next_rreq       <= rreq_valid and ready_for_rreq;
    ready_for_rreq  <= (not tmp_valid) or (in_AXI_ARREADY and rreq_ready);
    valid_length    <= '1' when rreq_len /= 0 and rreq_len(USER_LEN_WIDTH-1) = '0' else '0';

    out_AXI_ARLEN   <= tmp_len;   -- Byte length
    out_AXI_ARADDR  <= tmp_addr;  -- Byte address
    out_AXI_ARVALID <= tmp_valid and rreq_ready;

    byte_addr_branch : if (BYTE_ADDR_MODE = 1) generate
    begin
        rreq_addr_byte <= RESIZE(rreq_addr, BUS_ADDR_WIDTH);
        rreq_len_byte  <= rreq_len;
    end generate byte_addr_branch;
    word_addr_branch : if (BYTE_ADDR_MODE = 0) generate
    begin
        rreq_addr_byte <= SHIFT_LEFT(RESIZE(rreq_addr, BUS_ADDR_WIDTH), USER_ADDR_ALIGN);
        rreq_len_byte  <= SHIFT_LEFT(rreq_len, USER_ADDR_ALIGN);
    end generate word_addr_branch;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                tmp_addr  <= (others => '0');
                tmp_len   <= (others => '0');
            elsif ACLK_EN = '1' then
                if (next_rreq = '1') then
                    tmp_addr  <= TARGET_ADDR + rreq_addr_byte;
                    tmp_len   <= rreq_len_byte - 1;
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                tmp_valid  <= '0';
            elsif ACLK_EN = '1' then
                if (next_rreq and valid_length) = '1' then
                    tmp_valid <= '1';
                elsif (in_AXI_ARREADY and rreq_ready) = '1' then
                    tmp_valid <= '0';
                end if;
            end if;
        end if;
    end process;

    -- end of ARADDR PREPROCESSOR
    -- ===================================================================
    
    -- R channel regslice 

    -- R channel fifo
    buff_rdata : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        MEM_STYLE         => PREFERRED_IMPL,
        DATA_WIDTH        => BUS_DATA_WIDTH + 2,
        ADDR_WIDTH        => log2(RBUFF_DEPTH),
        DEPTH             => RBUFF_DEPTH)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => out_AXI_RREADY,
        if_write          => in_AXI_RVALID,
        if_din            => in_AXI_RDATA_PACK,
        if_empty_n        => local_AXI_RVALID,
        if_read           => local_AXI_RREADY,
        if_dout           => out_AXI_RDATA_PACK,
        if_num_data_valid => local_AXI_RFIFONUM);

    in_AXI_RDATA_PACK     <= in_AXI_RLAST & in_AXI_RDATA;
    local_AXI_RDATA       <= out_AXI_RDATA_PACK(BUS_DATA_WIDTH-1 downto 0);
    local_AXI_RLAST       <= out_AXI_RDATA_PACK(BUS_DATA_WIDTH+1 downto BUS_DATA_WIDTH);

    -- Outstanding control
    out_BURST_RREADY      <= ready_for_outstanding;
    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                ready_for_outstanding  <= '1';
            elsif ACLK_EN = '1' then
                if (local_AXI_RREADY = '1' and local_AXI_RVALID = '1') then
                    ready_for_outstanding <= local_AXI_RLAST(1);
                else
                    ready_for_outstanding <= '0';
                end if;
            end if;
        end if;
    end process;

    -- ===================================================================
    -- start of RDATA PREPROCESSOR
    bus_equal_gen : if (USER_DATA_ALIGN = BUS_DATA_WIDTH) generate
    begin
        rreq_ready       <= '1';
        -- R channel regslice ?  disabled
        local_AXI_RREADY <= in_HLS_RREADY;
        out_HLS_RDATA    <= local_AXI_RDATA(USER_DW-1 downto 0);
        out_HLS_RVALID   <= local_AXI_RVALID;
        out_HLS_RFIFONUM <= RESIZE(local_AXI_RFIFONUM, USER_RFIFONUM_WIDTH); 

    end generate bus_equal_gen;

    bus_wide_gen : if (USER_DATA_ALIGN < BUS_DATA_WIDTH) generate
        constant TOTAL_SPLIT    : INTEGER := BUS_DATA_WIDTH / USER_DATA_ALIGN;
        constant SPLIT_ALIGN    : INTEGER := log2(TOTAL_SPLIT);

        signal  tmp_addr_end    : UNSIGNED(BUS_ADDR_ALIGN-1 downto 0);
        signal  offset_full_n   : STD_LOGIC;
        signal  offset_write    : STD_LOGIC;
        signal  offset_valid    : STD_LOGIC;
        signal  next_offset     : STD_LOGIC;

        signal  start_offset    : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  end_offset      : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  head_offset     : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  tail_offset     : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  offset_pack_in  : UNSIGNED(2*SPLIT_ALIGN-1 downto 0);
        signal  offset_pack_out : UNSIGNED(2*SPLIT_ALIGN-1 downto 0);

        signal  rdata_pack      : UNSIGNED(BUS_DATA_WIDTH downto 0);
        signal  rdata_data      : UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
        signal  rdata_last      : STD_LOGIC;
        signal  rdata_valid     : STD_LOGIC;
        signal  next_rdata      : STD_LOGIC;
        signal  data_buf        : UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
        signal  data_valid      : STD_LOGIC;

        signal  rdata_nvalid    : UNSIGNED(USER_RFIFONUM_WIDTH-1 downto 0);
        signal  data_nvalid     : UNSIGNED(SPLIT_ALIGN downto 0);
        signal  split_nvalid    : UNSIGNED(SPLIT_ALIGN downto 0);

        signal  split_cnt_end   : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  split_cnt       : UNSIGNED(SPLIT_ALIGN-1 downto 0);
        signal  split_cnt_buf   : UNSIGNED(SPLIT_ALIGN-1 downto 0);

        signal  ready_for_data  : BOOLEAN;
        signal  first_data_pred : BOOLEAN;
        signal  first_data      : BOOLEAN;
        signal  last_data       : BOOLEAN;
        
        signal  split_valid     : BOOLEAN;
        signal  first_split     : BOOLEAN;
        signal  first_split_pred : BOOLEAN;
        signal  next_split      : BOOLEAN;
        signal  last_split      : BOOLEAN;

    begin

        rreq_offset : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => 2*SPLIT_ALIGN,
            ADDR_WIDTH        => log2(NUM_READ_OUTSTANDING),
            DEPTH             => NUM_READ_OUTSTANDING)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => offset_full_n,
            if_write          => offset_write,
            if_din            => offset_pack_in,
            if_empty_n        => offset_valid,
            if_read           => next_offset,
            if_dout           => offset_pack_out,
            if_num_data_valid => open);

        rs_rdata_int : pointpillars_cnn_gmem_m_axi_reg_slice
        generic map (
            DATA_WIDTH        => BUS_DATA_WIDTH + 1)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            s_data            => out_AXI_RDATA_PACK(BUS_DATA_WIDTH downto 0),
            s_valid           => local_AXI_RVALID,
            s_ready           => local_AXI_RREADY,
            m_data            => rdata_pack,
            m_valid           => rdata_valid,
            m_ready           => next_rdata);

        rreq_ready       <= '1' when offset_full_n = '1' or offset_write = '0' else '0';
        tmp_addr_end     <= tmp_addr(BUS_ADDR_ALIGN-1 downto 0) + tmp_len(BUS_ADDR_ALIGN-1 downto 0);
        start_offset     <= tmp_addr(BUS_ADDR_ALIGN-1 downto USER_ADDR_ALIGN);
        end_offset       <= tmp_addr_end(BUS_ADDR_ALIGN-1 downto USER_ADDR_ALIGN);
        offset_write     <= tmp_valid and in_AXI_ARREADY;
        next_offset      <= rdata_last and next_rdata;

        offset_pack_in   <= start_offset & end_offset;
        head_offset      <= offset_pack_out(2*SPLIT_ALIGN-1 downto SPLIT_ALIGN);
        tail_offset      <= offset_pack_out(SPLIT_ALIGN-1 downto 0);

        -- regslice io ?  no
        out_HLS_RDATA    <= data_buf(USER_DW-1 downto 0);
        out_HLS_RVALID   <= data_valid;
        out_HLS_RFIFONUM <= rdata_nvalid + data_nvalid;
        ready_for_data   <= data_valid = '0' or in_HLS_RREADY = '1';

        next_rdata       <= '1' when (rdata_valid = '1') and last_split else '0';
        rdata_last       <= rdata_pack(BUS_DATA_WIDTH);
        rdata_data       <= rdata_pack(BUS_DATA_WIDTH-1 downto 0);
        first_data       <= first_data_pred and split_valid;
        last_data        <= (rdata_last = '1') and split_valid;

        split_valid      <= (rdata_valid and offset_valid) = '1';
        next_split       <= split_valid and ready_for_data;

        first_split      <= first_split_pred and next_split;
        last_split       <= (split_cnt = split_cnt_end) and next_split;

        split_cnt        <= head_offset when first_data and first_split_pred else
                            split_cnt_buf;
        split_cnt_end    <= tail_offset when last_data else
                            TO_UNSIGNED(TOTAL_SPLIT - 1, SPLIT_ALIGN);

        split_nvalid     <= RESIZE(tail_offset, SPLIT_ALIGN+1) - head_offset + 1  when first_data and last_data else
                            RESIZE(tail_offset, SPLIT_ALIGN+1) + 1                when last_data                else
                            TO_UNSIGNED(TOTAL_SPLIT, SPLIT_ALIGN+1) - head_offset when first_data               else
                            TO_UNSIGNED(TOTAL_SPLIT, SPLIT_ALIGN+1);

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_data_pred <= true;
                elsif ACLK_EN = '1' then
                    if next_offset = '1' then
                        first_data_pred <= true;
                    elsif (last_split) then
                        first_data_pred <= false;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_split_pred <= true;
                    split_cnt_buf <= (others => '0');
                elsif ACLK_EN = '1' then
                    if last_split then
                        first_split_pred <= true;
                        split_cnt_buf <= (others => '0');
                    elsif next_split then
                        first_split_pred <= false;
                        split_cnt_buf <= split_cnt + 1;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if ACLK_EN = '1' then
                    if first_split and first_data then
                        data_buf <= SHIFT_RIGHT(rdata_data, to_integer(head_offset)*USER_DATA_WIDTH);
                    elsif first_split then
                        data_buf <= rdata_data;
                    elsif next_split then
                        data_buf <= SHIFT_RIGHT(data_buf, USER_DATA_WIDTH);
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_valid <= '0';
                elsif ACLK_EN = '1' then
                    if next_split then
                        data_valid <= '1';
                    elsif ready_for_data then
                        data_valid <= '0';
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_nvalid <= (others=>'0');
                elsif ACLK_EN = '1' then
                    if first_split then
                        data_nvalid <= split_nvalid;
                    elsif next_split then
                        data_nvalid <= data_nvalid - 1;
                    elsif ready_for_data then
                        data_nvalid <= (others=>'0');
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    rdata_nvalid <= (others=>'0');
                elsif ACLK_EN = '1' then
                    if rdata_valid = '0' then
                        rdata_nvalid <= (others=>'0');
                    elsif local_AXI_RREADY = '0' then 
                        rdata_nvalid <= SHIFT_LEFT(RESIZE(local_AXI_RFIFONUM+1, USER_RFIFONUM_WIDTH), SPLIT_ALIGN);
                    else
                        rdata_nvalid <= SHIFT_LEFT(RESIZE(local_AXI_RFIFONUM, USER_RFIFONUM_WIDTH), SPLIT_ALIGN);
                    end if;
                end if;
            end if;
        end process;

    end generate bus_wide_gen; 

    bus_narrow_gen : if (USER_DATA_ALIGN > BUS_DATA_WIDTH) generate
        constant TOTAL_PADS     : INTEGER := USER_DATA_ALIGN / BUS_DATA_WIDTH;
        constant PAD_ALIGN      : INTEGER := log2(TOTAL_PADS);

        signal  data_buf        : UNSIGNED(USER_DATA_WIDTH-1 downto 0);
        signal  data_nvalid     : UNSIGNED(PAD_ALIGN downto 0);
        signal  data_valid      : STD_LOGIC;
        signal  pad_oh          : UNSIGNED(TOTAL_PADS - 1 downto 0);
        signal  pad_oh_reg      : UNSIGNED(TOTAL_PADS - 1 downto 0);
       
        signal  rdata_num_vld   : UNSIGNED(USER_RFIFONUM_WIDTH - 1 downto 0);
        signal  ready_for_data  : BOOLEAN;
        signal  next_pad        : BOOLEAN;
        signal  first_pad       : BOOLEAN;
        signal  last_pad        : BOOLEAN;
    begin

        rreq_ready       <= '1';
        local_AXI_RREADY <= '1' when next_pad else '0';
        rdata_num_vld    <= RESIZE(local_AXI_RFIFONUM(log2(RBUFF_DEPTH) downto PAD_ALIGN), USER_RFIFONUM_WIDTH) + SHIFT_RIGHT(local_AXI_RFIFONUM(PAD_ALIGN-1 downto 0) + data_nvalid, PAD_ALIGN);

        -- regslice io ?  no
        out_HLS_RDATA    <= data_buf(USER_DW-1 downto 0);
        out_HLS_RVALID   <= data_valid;
        out_HLS_RFIFONUM <= rdata_num_vld;
        ready_for_data   <= data_valid = '0' or in_HLS_RREADY = '1';

        next_pad         <= local_AXI_RVALID = '1' and ready_for_data;
        last_pad         <= pad_oh(TOTAL_PADS - 1) = '1';

        pad_oh           <= (others => '0')            when local_AXI_RVALID = '0' else
                            TO_UNSIGNED(1, TOTAL_PADS) when first_pad      else
                            pad_oh_reg;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    pad_oh_reg <= (others => '0');
                elsif ACLK_EN = '1' then
                    if next_pad then
                        pad_oh_reg <= pad_oh(TOTAL_PADS - 2 downto 0) & '0';
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_pad <= true;
                elsif ACLK_EN = '1' then
                    if next_pad and not last_pad then
                        first_pad <= false;
                    elsif next_pad and last_pad then
                        first_pad <= true;
                    end if;
                end if;
            end if;
        end process;

        data_gen : for i in 0 to TOTAL_PADS-1 generate
        begin
            process (ACLK)
            begin
                if (ACLK'event and ACLK = '1') then
                    if ACLK_EN = '1' then
                        if pad_oh(i) = '1' and ready_for_data then
                            data_buf((i+1)*BUS_DATA_WIDTH - 1 downto i*BUS_DATA_WIDTH) <= local_AXI_RDATA;
                        end if;
                    end if;
                end if;
            end process;
        end generate data_gen;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_valid <= '0';
                elsif ACLK_EN = '1' then
                    if next_pad and last_pad then
                        data_valid <= '1';
                    elsif ready_for_data then
                        data_valid <= '0';
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_nvalid <= (others=>'0');
                elsif ACLK_EN = '1' then
                    if first_pad then
                        data_nvalid <= TO_UNSIGNED(1,PAD_ALIGN+1);
                    elsif next_pad then
                        data_nvalid <= data_nvalid + 1;
                    end if;
                end if;
            end if;
        end process;
    
    end generate bus_narrow_gen;

    -- end of RDATA PREPROCESSOR
    -- ==================================================================
end architecture behave;

library IEEE;
use IEEE.STD_LOGIC_1164.all;
use IEEE.NUMERIC_STD.all;

entity pointpillars_cnn_gmem_m_axi_store is
    generic (
        CONSERVATIVE           : INTEGER := 0;
        C_TARGET_ADDR          : INTEGER := 16#00000000#;
        NUM_WRITE_OUTSTANDING  : INTEGER := 2;
        MAX_WRITE_BURST_LENGTH : INTEGER := 16;
        BUS_ADDR_WIDTH         : INTEGER := 32;
        BUS_DATA_WIDTH         : INTEGER := 32;
        BYTE_ADDR_MODE         : INTEGER := 0;
        USER_DW                : INTEGER := 16;
        USER_AW                : INTEGER := 32;
        USER_LEN_WIDTH         : INTEGER := 32;
        USER_MAXREQS           : INTEGER := 16;
        BUFFER_IMPL            : STRING  := "auto"); 
    port (
        ACLK                   : in  STD_LOGIC;
        ARESET                 : in  STD_LOGIC;
        ACLK_EN                : in  STD_LOGIC;
        out_AXI_AWADDR         : out UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
        out_AXI_AWLEN          : out UNSIGNED(USER_LEN_WIDTH-1 downto 0);
        out_AXI_AWVALID        : out STD_LOGIC;
        in_AXI_AWREADY         : in  STD_LOGIC;
        in_BURST_AWLEN         : in  UNSIGNED(7 downto 0);
        in_BURST_AWVALID       : in  STD_LOGIC;
        out_BURST_WLEN         : out UNSIGNED(7 downto 0);
        out_BURST_WVALID       : out STD_LOGIC;
        in_BURST_WREADY        : in  STD_LOGIC;
        out_AXI_WDATA          : out UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
        out_AXI_WSTRB          : out UNSIGNED(BUS_DATA_WIDTH/8-1 downto 0);
        out_AXI_WVALID         : out STD_LOGIC;
        in_AXI_WREADY          : in  STD_LOGIC;
        in_AXI_BVALID          : in  STD_LOGIC;
        out_AXI_BREADY         : out STD_LOGIC;
        in_HLS_AWADDR          : in  UNSIGNED(USER_AW-1 downto 0);
        in_HLS_AWLEN           : in  UNSIGNED(USER_LEN_WIDTH-1 downto 0); 
        in_HLS_AWVALID         : in  STD_LOGIC;
        out_HLS_AWREADY        : out STD_LOGIC;
        in_HLS_WDATA           : in  UNSIGNED(USER_DW-1 downto 0);
        in_HLS_WSTRB           : in  UNSIGNED(USER_DW/8-1 downto 0);
        in_HLS_WVALID          : in  STD_LOGIC;
        out_HLS_WREADY         : out STD_LOGIC;
        out_HLS_BVALID         : out STD_LOGIC;
        in_HLS_BREADY          : in  STD_LOGIC);
end entity pointpillars_cnn_gmem_m_axi_store;

architecture behave of pointpillars_cnn_gmem_m_axi_store is
    ------------------------Task and function--------------
    function max (x : INTEGER; 
                  y : INTEGER) return INTEGER is
        variable r : INTEGER;
    begin
        r := y;
        if (x > y) then
            r := x;
        end if;
        return r;
    end function max;

    function calc_data_width (x : INTEGER) return INTEGER is
        variable y : INTEGER;
    begin
        y := 8;
        while y < x loop
            y := y * 2;
        end loop;
        return y;
    end function calc_data_width;

    function log2 (x : INTEGER) return INTEGER is
        variable n, m : INTEGER;
    begin
        n := 0;
        m := 1;
        while m < x loop
            n := n + 1;
            m := m * 2;
        end loop;
        return n;
    end function log2;

    function cond_sel (cond : BOOLEAN; x : INTEGER; y : INTEGER) return INTEGER is
        variable ret : INTEGER;
    begin
        if (cond) then
            ret := x;
        else
            ret := y;
        end if;
        return ret;
    end function cond_sel;
    ------------------------Parameter----------------------
    constant USER_DATA_WIDTH  : INTEGER := calc_data_width(USER_DW);
    constant USER_DATA_BYTES  : INTEGER := USER_DATA_WIDTH / 8;
    constant USER_DATA_ALIGN  : INTEGER := USER_DATA_WIDTH;
    constant USER_ADDR_ALIGN  : INTEGER := log2(USER_DATA_BYTES);
    constant WREQ_PACK_WIDTH  : INTEGER := USER_AW + USER_LEN_WIDTH;
    constant BUS_DATA_BYTES   : INTEGER := BUS_DATA_WIDTH / 8;
    constant BUS_ADDR_ALIGN   : INTEGER := log2(BUS_DATA_BYTES);
    -- wdata buffer size 
    constant WBUFF_DEPTH      : INTEGER := cond_sel((USER_DATA_ALIGN = BUS_DATA_WIDTH), 
                                                    2 * MAX_WRITE_BURST_LENGTH, MAX_WRITE_BURST_LENGTH);
    constant PREFERRED_IMPL   : STRING  := "shiftreg" when (WBUFF_DEPTH <= 32) else BUFFER_IMPL;
    constant BURST_LEN_WIDTH  : INTEGER := max(log2(WBUFF_DEPTH + 1), 8);
    constant TARGET_ADDR      : INTEGER := (C_TARGET_ADDR/(2**USER_ADDR_ALIGN))*(2**USER_ADDR_ALIGN);
    ------------------------Local signal-------------------
    signal next_wreq      : STD_LOGIC;
    signal ready_for_wreq : STD_LOGIC;
    signal wreq_ready     : STD_LOGIC;
    signal wreq_valid     : STD_LOGIC;
    signal valid_length   : STD_LOGIC;

    signal wreq_addr      : UNSIGNED(USER_AW-1 downto 0);
    signal wreq_len       : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal wreq_addr_byte : UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
    signal wreq_len_byte  : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal tmp_addr       : UNSIGNED(BUS_ADDR_WIDTH-1 downto 0);
    signal tmp_len        : UNSIGNED(USER_LEN_WIDTH-1 downto 0);
    signal tmp_valid      : STD_LOGIC;

    signal out_AXI_WDATA_PACK   : UNSIGNED(BUS_DATA_WIDTH+BUS_DATA_BYTES-1 downto 0);
    signal local_AXI_WDATA_PACK : UNSIGNED(BUS_DATA_WIDTH+BUS_DATA_BYTES-1 downto 0);
    signal local_AXI_WVALID : STD_LOGIC;
    signal local_AXI_WREADY : STD_LOGIC;

    signal wrsp_ready     : STD_LOGIC;
    signal wrsp_valid     : STD_LOGIC;
    signal wrsp_read      : STD_LOGIC;
    signal wrsp_type      : UNSIGNED(0 downto 0);
    signal in_wrsp_type   : UNSIGNED(0 downto 0);
    signal ursp_ready     : STD_LOGIC;
    signal ursp_write     : STD_LOGIC;

    signal in_wreq_pack   : UNSIGNED(WREQ_PACK_WIDTH-1 downto 0);
    signal out_wreq_pack  : UNSIGNED(WREQ_PACK_WIDTH-1 downto 0);
    -- regslice io ?  no 
    -- enable regslice on W channel  no 
    component pointpillars_cnn_gmem_m_axi_fifo is
        generic (
            MEM_STYLE         : STRING  := "shiftreg";
            DATA_WIDTH        : INTEGER := 8;
            ADDR_WIDTH        : INTEGER := 4;
            DEPTH             : INTEGER := 16);
        port (
            clk               : in  STD_LOGIC;
            reset             : in  STD_LOGIC;
            clk_en            : in  STD_LOGIC;
            if_full_n         : out STD_LOGIC;
            if_write          : in  STD_LOGIC;
            if_din            : in  UNSIGNED(DATA_WIDTH-1 downto 0);
            if_empty_n        : out STD_LOGIC;
            if_read           : in  STD_LOGIC;
            if_dout           : out UNSIGNED(DATA_WIDTH-1 downto 0);
            if_num_data_valid : out UNSIGNED(ADDR_WIDTH downto 0));
    end component pointpillars_cnn_gmem_m_axi_fifo;


begin
    -- Instantiation

    fifo_wreq : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        DATA_WIDTH        => WREQ_PACK_WIDTH,
        ADDR_WIDTH        => log2(USER_MAXREQS),
        DEPTH             => USER_MAXREQS)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => out_HLS_AWREADY,
        if_write          => in_HLS_AWVALID,
        if_din            => in_wreq_pack,
        if_empty_n        => wreq_valid,
        if_read           => next_wreq,
        if_dout           => out_wreq_pack,
        if_num_data_valid => open);

    -- ===================================================================
    -- start of AWADDR PREPROCESSOR
    in_wreq_pack    <= in_HLS_AWLEN & in_HLS_AWADDR;
    wreq_len        <= out_wreq_pack(USER_AW + USER_LEN_WIDTH-1 downto USER_AW);
    wreq_addr       <= out_wreq_pack(USER_AW - 1 downto 0);
    next_wreq       <= wreq_valid and ready_for_wreq and wrsp_ready;
    ready_for_wreq  <= not tmp_valid or (in_AXI_AWREADY and wreq_ready);
    valid_length    <= '1' when wreq_len /= 0 and wreq_len(USER_LEN_WIDTH-1) = '0' else '0';
   
    out_AXI_AWLEN   <= tmp_len;   -- Byte length
    out_AXI_AWADDR  <= tmp_addr;  -- Byte address
    out_AXI_AWVALID <= tmp_valid and wreq_ready;

    byte_addr_branch : if (BYTE_ADDR_MODE = 1) generate
    begin
        wreq_addr_byte <= RESIZE(wreq_addr, BUS_ADDR_WIDTH);
        wreq_len_byte  <= wreq_len;
    end generate byte_addr_branch;
    word_addr_branch : if (BYTE_ADDR_MODE = 0) generate
    begin
        wreq_addr_byte <= SHIFT_LEFT(RESIZE(wreq_addr, BUS_ADDR_WIDTH), USER_ADDR_ALIGN);
        wreq_len_byte  <= SHIFT_LEFT(wreq_len, USER_ADDR_ALIGN);
    end generate word_addr_branch;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                tmp_addr  <= (others => '0');
                tmp_len   <= (others => '0');
            elsif ACLK_EN = '1' then
                if (next_wreq = '1') then
                    tmp_addr  <= TARGET_ADDR + wreq_addr_byte;
                    tmp_len   <= wreq_len_byte - 1;
                end if;
            end if;
        end if;
    end process;

    process (ACLK)
    begin
        if (ACLK'event and ACLK = '1') then
            if (ARESET = '1') then
                tmp_valid  <= '0';
            elsif ACLK_EN = '1' then
                if (next_wreq and valid_length) = '1' then
                    tmp_valid <= '1';
                elsif (in_AXI_AWREADY and wreq_ready) = '1' then
                    tmp_valid <= '0';
                end if;
            end if;
        end if;
    end process;

    -- end of AWADDR PREPROCESSOR
    -- ===================================================================
    -- ===================================================================
    -- start of WRITE BURST throttling control

    aggressive_gen : if (CONSERVATIVE = 0) generate
    begin
        out_BURST_WLEN   <= (others => '0');
        out_BURST_WVALID <= '0';
    end generate aggressive_gen;
    
    conservative_gen : if (CONSERVATIVE /= 0) generate
        signal local_BURST_WLEN   : UNSIGNED(7 downto 0);
        signal local_BURST_WVALID : STD_LOGIC;
        signal next_burst         : STD_LOGIC;
        signal burst_len          : UNSIGNED(7 downto 0);
        signal burst_valid        : STD_LOGIC;
        signal burst_ready        : STD_LOGIC;
        signal beat_write         : STD_LOGIC;
        signal num_beat_pred_br11 : UNSIGNED(BURST_LEN_WIDTH downto 0);
        signal num_beat_pred_br10 : UNSIGNED(BURST_LEN_WIDTH downto 0);
        signal num_beat_pred_br01 : UNSIGNED(BURST_LEN_WIDTH downto 0);
        signal num_beat_pred_br00 : UNSIGNED(BURST_LEN_WIDTH downto 0);
        signal num_beat_pred      : UNSIGNED(BURST_LEN_WIDTH-1 downto 0);
        signal num_beat_cnt       : UNSIGNED(BURST_LEN_WIDTH-1 downto 0);
    begin
        
        fifo_burst : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => 8,
            ADDR_WIDTH        => log2(NUM_WRITE_OUTSTANDING),
            DEPTH             => NUM_WRITE_OUTSTANDING)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => open,
            if_write          => in_BURST_AWVALID,
            if_din            => in_BURST_AWLEN,
            if_empty_n        => burst_valid,
            if_read           => next_burst,
            if_dout           => burst_len,
            if_num_data_valid => open);
        
        out_BURST_WLEN        <= local_BURST_WLEN;
        out_BURST_WVALID      <= local_BURST_WVALID;
        burst_ready           <= (not local_BURST_WVALID) or in_BURST_WREADY;
        next_burst            <= burst_valid and burst_ready and (not num_beat_pred_br10(BURST_LEN_WIDTH));
        beat_write            <= local_AXI_WVALID and local_AXI_WREADY;

        num_beat_pred_br11    <= ('0' & num_beat_cnt) - burst_len;
        num_beat_pred_br10    <= ('0' & num_beat_cnt) - burst_len - 1;
        num_beat_pred_br01    <= ('0' & num_beat_cnt) + 1;
        num_beat_pred_br00    <= ('0' & num_beat_cnt);
        num_beat_pred         <= num_beat_pred_br11(BURST_LEN_WIDTH-1 downto 0) when (next_burst = '1' and beat_write = '1') else
                                 num_beat_pred_br10(BURST_LEN_WIDTH-1 downto 0) when (next_burst = '1' and beat_write = '0') else
                                 num_beat_pred_br01(BURST_LEN_WIDTH-1 downto 0) when (next_burst = '0' and beat_write = '1') else
                                 num_beat_pred_br00(BURST_LEN_WIDTH-1 downto 0);

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    local_BURST_WLEN <= (others=>'0');
                elsif ACLK_EN = '1' then
                    if next_burst = '1' then
                        local_BURST_WLEN <= burst_len;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    local_BURST_WVALID <= '0';
                elsif ACLK_EN = '1' then
                    if next_burst = '1' then
                        local_BURST_WVALID <= '1';
                    elsif in_BURST_WREADY = '1' then
                        local_BURST_WVALID <= '0';
                    end if;
                end if;
            end if;
        end process;
    
        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    num_beat_cnt <= (others=>'0');
                elsif ACLK_EN = '1' then
                    num_beat_cnt <= num_beat_pred;
                end if;
            end if;
        end process;
    
    end generate conservative_gen;

    -- end of WRITE BURST throttling control
    -- ===================================================================
    -- W channel regslice 

    -- W channel fifo
    buff_wdata : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        MEM_STYLE         => PREFERRED_IMPL,
        DATA_WIDTH        => BUS_DATA_WIDTH + BUS_DATA_BYTES,
        ADDR_WIDTH        => log2(WBUFF_DEPTH),
        DEPTH             => WBUFF_DEPTH)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => local_AXI_WREADY,
        if_write          => local_AXI_WVALID,
        if_din            => local_AXI_WDATA_PACK,
        if_empty_n        => out_AXI_WVALID,
        if_read           => in_AXI_WREADY,
        if_dout           => out_AXI_WDATA_PACK,
        if_num_data_valid => open);

    -- ===================================================================
    -- start of WDATA PREPROCESSOR
    out_AXI_WDATA         <= out_AXI_WDATA_PACK(BUS_DATA_WIDTH-1 downto 0);
    out_AXI_WSTRB         <= out_AXI_WDATA_PACK(BUS_DATA_WIDTH+BUS_DATA_BYTES-1 downto BUS_DATA_WIDTH);
    bus_equal_gen : if (USER_DATA_ALIGN = BUS_DATA_WIDTH) generate
    begin
        local_AXI_WDATA_PACK <= RESIZE(in_HLS_WSTRB, BUS_DATA_BYTES) & RESIZE(in_HLS_WDATA, BUS_DATA_WIDTH);
        local_AXI_WVALID  <= in_HLS_WVALID;
        out_HLS_WREADY    <= local_AXI_WREADY;
        wreq_ready        <= '1';
    end generate bus_equal_gen;

    bus_wide_gen : if (USER_DATA_ALIGN < BUS_DATA_WIDTH) generate
        constant TOTAL_PADS     : INTEGER := BUS_DATA_WIDTH / USER_DATA_ALIGN;
        constant PAD_ALIGN      : INTEGER := log2(TOTAL_PADS);
        constant BEAT_LEN_WIDTH : INTEGER := USER_LEN_WIDTH - BUS_ADDR_ALIGN;
        constant OFFSET_PACK_WIDTH : INTEGER := 2*PAD_ALIGN + BEAT_LEN_WIDTH;
        constant WBUFF_IN_DEPTH : INTEGER := MAX_WRITE_BURST_LENGTH * TOTAL_PADS;

        function decoder (din : UNSIGNED(PAD_ALIGN-1 downto 0)) return UNSIGNED is 
            variable dout : UNSIGNED(TOTAL_PADS-1 downto 0);
        begin
            dout := (others=>'0');
            if ( not (din = 0)) then
                dout(TO_INTEGER(din)-1 downto 0) := (others=>'1');
            end if;
            return dout;
        end function decoder;

        signal  tmp_addr_end    : UNSIGNED(BUS_ADDR_ALIGN-1 downto 0);
        signal  offset_full_n   : STD_LOGIC;
        signal  offset_write    : STD_LOGIC;
        signal  offset_empty_n  : STD_LOGIC;
        signal  offset_read     : STD_LOGIC;
        signal  offset_valid    : STD_LOGIC;
        signal  next_offset     : STD_LOGIC;

        signal  offset_pack_in  : UNSIGNED(OFFSET_PACK_WIDTH-1 downto 0);
        signal  offset_pack_out : UNSIGNED(OFFSET_PACK_WIDTH-1 downto 0);
        signal  offset_pack_buf : UNSIGNED(OFFSET_PACK_WIDTH-1 downto 0);
        signal  start_offset    : UNSIGNED(PAD_ALIGN-1 downto 0);
        signal  end_offset      : UNSIGNED(PAD_ALIGN-1 downto 0);
        signal  head_offset     : UNSIGNED(PAD_ALIGN-1 downto 0);
        signal  tail_offset     : UNSIGNED(PAD_ALIGN-1 downto 0);
        signal  align_len       : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  wdata_len       : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  total_len       : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  beat_len        : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  beat_len_cnt    : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);

        signal  data_buf        : UNSIGNED(BUS_DATA_WIDTH-1 downto 0);
        signal  strb_buf        : UNSIGNED(BUS_DATA_BYTES-1 downto 0);
        signal  data_valid      : STD_LOGIC;

        signal  tmp_wdata       : UNSIGNED(USER_DATA_WIDTH-1 downto 0);
        signal  tmp_wstrb       : UNSIGNED(USER_DATA_BYTES-1 downto 0);

        signal  in_wdata_pack   : UNSIGNED(USER_DW+USER_DW/8-1 downto 0);
        signal  in_wdata_vld    : STD_LOGIC;
        signal  out_wdata_rdy   : STD_LOGIC;
        
        signal  local_HLS_WDATA_PACK : UNSIGNED(USER_DW+USER_DW/8-1 downto 0);
        signal  local_HLS_WVALID : STD_LOGIC;
        signal  local_HLS_WREADY : STD_LOGIC;

        signal  add_head        : UNSIGNED(TOTAL_PADS-1 downto 0); 
        signal  add_tail        : UNSIGNED(TOTAL_PADS-1 downto 0); 
        signal  head_pad_sel    : UNSIGNED(TOTAL_PADS-1 downto 0); 
        signal  tail_pad_sel    : UNSIGNED(0 to TOTAL_PADS-1);
        signal  pad_oh          : UNSIGNED(TOTAL_PADS - 1 downto 0);
        signal  pad_oh_reg      : UNSIGNED(TOTAL_PADS - 1 downto 0);
        signal  first_pad       : BOOLEAN;
        signal  next_pad        : BOOLEAN;
        signal  last_pad        : BOOLEAN;

        signal  first_beat_set  : BOOLEAN;
        signal  last_beat_set   : BOOLEAN;
        signal  last_beat_pred  : BOOLEAN;
        signal  single_beat_pred: BOOLEAN;
        signal  single_beat     : BOOLEAN;
        signal  ready_for_data  : BOOLEAN;
        signal  first_beat      : BOOLEAN;
        signal  next_beat       : BOOLEAN;
        signal  last_beat       : BOOLEAN;
    begin
        wreq_offset : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => OFFSET_PACK_WIDTH,
            ADDR_WIDTH        => log2(NUM_WRITE_OUTSTANDING),
            DEPTH             => NUM_WRITE_OUTSTANDING)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => offset_full_n,
            if_write          => offset_write,
            if_din            => offset_pack_in,
            if_empty_n        => offset_empty_n,
            if_read           => offset_read,
            if_dout           => offset_pack_out,
            if_num_data_valid => open);

        buff_wdata_in : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => USER_DW + USER_DW/8,
            ADDR_WIDTH        => log2(WBUFF_IN_DEPTH),
            DEPTH             => WBUFF_IN_DEPTH)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => out_wdata_rdy,
            if_write          => in_wdata_vld,
            if_din            => in_wdata_pack,
            if_empty_n        => local_HLS_WVALID,
            if_read           => local_HLS_WREADY,
            if_dout           => local_HLS_WDATA_PACK,
            if_num_data_valid => open);
        
        wreq_ready       <= '1' when offset_full_n = '1' or offset_write = '0' else '0';
        tmp_addr_end     <= tmp_addr(BUS_ADDR_ALIGN-1 downto 0) + tmp_len(BUS_ADDR_ALIGN-1 downto 0);
        start_offset     <= tmp_addr(BUS_ADDR_ALIGN-1 downto USER_ADDR_ALIGN);
        end_offset       <= not(tmp_addr_end(BUS_ADDR_ALIGN-1 downto USER_ADDR_ALIGN));
        align_len        <= RESIZE(SHIFT_RIGHT(tmp_len + tmp_addr(BUS_ADDR_ALIGN-1 downto 0), BUS_ADDR_ALIGN), BEAT_LEN_WIDTH);
        offset_write     <= tmp_valid and in_AXI_AWREADY;
        offset_read      <= not offset_valid or next_offset;

        offset_pack_in   <= start_offset & end_offset & align_len;
        head_offset      <= offset_pack_buf(OFFSET_PACK_WIDTH-1 downto BEAT_LEN_WIDTH+PAD_ALIGN);
        tail_offset      <= offset_pack_buf(BEAT_LEN_WIDTH+PAD_ALIGN-1 downto BEAT_LEN_WIDTH);
        total_len        <= offset_pack_buf(BEAT_LEN_WIDTH-1 downto 0);
        wdata_len        <= offset_pack_out(BEAT_LEN_WIDTH-1 downto 0);

        in_wdata_pack     <= in_HLS_WSTRB & in_HLS_WDATA;
        in_wdata_vld      <= in_HLS_WVALID;
        out_HLS_WREADY    <= out_wdata_rdy;
        local_AXI_WDATA_PACK <= strb_buf & data_buf;
        local_AXI_WVALID <= data_valid;
        local_HLS_WREADY <= '1' when offset_valid = '1' and ready_for_data else '0';

        tmp_wdata        <= RESIZE(local_HLS_WDATA_PACK(USER_DW-1 downto 0), USER_DATA_WIDTH);
        tmp_wstrb        <= RESIZE(local_HLS_WDATA_PACK(USER_DW+USER_DW/8-1 downto USER_DW), USER_DATA_BYTES);

        next_offset      <= '1' when last_beat and next_beat else '0';
        ready_for_data   <= data_valid = '0' or local_AXI_WREADY = '1';

        beat_len         <= total_len when first_beat else beat_len_cnt;
        first_beat       <= first_beat_set and (offset_valid = '1');
        last_beat        <= (single_beat or last_beat_set) and (offset_valid = '1');
        next_beat        <= local_HLS_WREADY = '1' and last_pad;

        single_beat_pred <= (wdata_len = 0);
        last_beat_pred   <= (beat_len = 1);

        next_pad         <= local_HLS_WREADY = '1' and local_HLS_WVALID = '1';
        last_pad         <= pad_oh(TOTAL_PADS - to_integer(tail_offset) - 1) = '1' when last_beat else
                            pad_oh(TOTAL_PADS - 1) = '1';
        head_pad_sel     <= decoder(head_offset);
        tail_pad_sel     <= decoder(tail_offset);
        pad_oh           <= (others => '0')                                                 when local_HLS_WVALID = '0' else
                            SHIFT_LEFT(TO_UNSIGNED(1, TOTAL_PADS), TO_INTEGER(head_offset)) when first_beat and first_pad else
                            TO_UNSIGNED(1, TOTAL_PADS)                                      when first_pad else
                            pad_oh_reg;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    single_beat <= false;
                    offset_pack_buf <= (others => '0');
                elsif ACLK_EN = '1' then
                    if offset_empty_n = '1' and offset_read = '1' then
                            single_beat <= single_beat_pred;
                            offset_pack_buf <= offset_pack_out;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    offset_valid <= '0';
                elsif ACLK_EN = '1' then
                    if offset_empty_n = '1' and offset_read = '1' then
                        offset_valid <= '1';
                    elsif next_offset = '1' then
                        offset_valid <= '0';
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_beat_set <= true;
                    last_beat_set  <= false;
                elsif ACLK_EN = '1' then
                    if next_offset = '1' then
                        first_beat_set <= true;
                        last_beat_set  <= false;
                    elsif next_beat then
                        first_beat_set <= false;
                        last_beat_set  <= last_beat_pred;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    beat_len_cnt <= (others => '0');
                elsif ACLK_EN = '1' then
                    if next_beat then
                        beat_len_cnt <= beat_len - 1;
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    pad_oh_reg <= (others => '0');
                elsif ACLK_EN = '1' then
                    if next_pad then
                        pad_oh_reg <= pad_oh(TOTAL_PADS - 2 downto 0) & '0';
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_pad <= true;
                elsif ACLK_EN = '1' then
                    if next_pad and not last_pad then
                        first_pad <= false;
                    elsif next_pad and last_pad then
                        first_pad <= true;
                    end if;
                end if;
            end if;
        end process;

        data_gen : for i in 0 to TOTAL_PADS - 1 generate
        begin
            add_head(i) <= '1' when head_pad_sel(i) = '1' and first_beat else '0';
            add_tail(i) <= '1' when tail_pad_sel(i) = '1' and last_beat else '0';

            process (ACLK)
            begin
                if (ACLK'event and ACLK = '1') then
                    if (ARESET = '1') then
                        data_buf((i+1)*USER_DATA_WIDTH - 1 downto i*USER_DATA_WIDTH) <= (others => '0'); 
                    elsif (ACLK_EN = '1') then
                        if (add_head(i) = '1' or add_tail(i) = '1') and ready_for_data then
                            data_buf((i+1)*USER_DATA_WIDTH - 1 downto i*USER_DATA_WIDTH) <= (others => '0');
                        elsif pad_oh(i) = '1' and ready_for_data then
                            data_buf((i+1)*USER_DATA_WIDTH - 1 downto i*USER_DATA_WIDTH) <= tmp_wdata;
                        end if;
                    end if;
                end if;
            end process;

            process (ACLK)
            begin
                if (ACLK'event and ACLK = '1') and ACLK_EN = '1' then
                    if (ARESET = '1') then
                        strb_buf((i+1)*USER_DATA_BYTES - 1 downto i*USER_DATA_BYTES) <= (others => '0');
                    elsif (ACLK_EN = '1') then
                        if (add_head(i) = '1' or add_tail(i) = '1') and ready_for_data then
                            strb_buf((i+1)*USER_DATA_BYTES - 1 downto i*USER_DATA_BYTES) <= (others => '0');
                        elsif pad_oh(i) = '1' and ready_for_data then
                            strb_buf((i+1)*USER_DATA_BYTES - 1 downto i*USER_DATA_BYTES) <= tmp_wstrb;
                        end if;
                    end if;
                end if;
            end process;
        end generate data_gen;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_valid <= '0';
                elsif ACLK_EN = '1' then
                    if next_beat then
                        data_valid <= '1';
                    elsif ready_for_data then
                        data_valid <= '0';
                    end if;
                end if;
            end if;
        end process;

    end generate bus_wide_gen; 

    bus_narrow_gen : if (USER_DATA_ALIGN > BUS_DATA_WIDTH) generate
        constant TOTAL_SPLIT    : INTEGER := USER_DATA_ALIGN / BUS_DATA_WIDTH;
        constant SPLIT_ALIGN    : INTEGER := log2(TOTAL_SPLIT);
        constant BEAT_LEN_WIDTH : INTEGER := USER_LEN_WIDTH - BUS_ADDR_ALIGN;
        constant WBUFF_IN_DEPTH : INTEGER := max(MAX_WRITE_BURST_LENGTH / TOTAL_SPLIT, 1);

        signal  offset_full_n   : STD_LOGIC;
        signal  offset_write    : STD_LOGIC;
        signal  offset_valid    : STD_LOGIC;
        signal  next_offset     : STD_LOGIC;

        signal  align_len       : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  beat_len        : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);
        signal  beat_len_cnt    : UNSIGNED(BEAT_LEN_WIDTH-1 downto 0);

        signal  data_buf        : UNSIGNED(USER_DATA_WIDTH - 1 downto 0);
        signal  strb_buf        : UNSIGNED(USER_DATA_BYTES - 1 downto 0);
        signal  data_valid      : STD_LOGIC;

        signal  tmp_wdata       : UNSIGNED(USER_DATA_WIDTH-1 downto 0);
        signal  tmp_wstrb       : UNSIGNED(USER_DATA_BYTES-1 downto 0);

        signal  in_wdata_pack   : UNSIGNED(USER_DW+USER_DW/8-1 downto 0);
        signal  in_wdata_vld    : STD_LOGIC;
        signal  out_wdata_rdy   : STD_LOGIC;
        
        signal  local_HLS_WDATA_PACK : UNSIGNED(USER_DW+USER_DW/8-1 downto 0);
        signal  local_HLS_WVALID : STD_LOGIC;
        signal  local_HLS_WREADY : STD_LOGIC;

        signal  split_cnt       : UNSIGNED(SPLIT_ALIGN - 1 downto 0);
        signal  first_split_pred: BOOLEAN;
        signal  first_split     : BOOLEAN;
        signal  next_split      : BOOLEAN;
        signal  last_split      : BOOLEAN;
        signal  ready_for_data  : BOOLEAN;
    begin
        -- instantiation
        wreq_offset : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => BEAT_LEN_WIDTH,
            ADDR_WIDTH        => log2(NUM_WRITE_OUTSTANDING),
            DEPTH             => NUM_WRITE_OUTSTANDING)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => offset_full_n,
            if_write          => offset_write,
            if_din            => align_len,
            if_empty_n        => offset_valid,
            if_read           => next_offset,
            if_dout           => beat_len,
            if_num_data_valid => open);

        buff_wdata_in : pointpillars_cnn_gmem_m_axi_fifo
        generic map (
            DATA_WIDTH        => USER_DW + USER_DW/8,
            ADDR_WIDTH        => log2(WBUFF_IN_DEPTH),
            DEPTH             => WBUFF_IN_DEPTH)
        port map (
            clk               => ACLK,
            reset             => ARESET,
            clk_en            => ACLK_EN,
            if_full_n         => out_wdata_rdy,
            if_write          => in_wdata_vld,
            if_din            => in_wdata_pack,
            if_empty_n        => local_HLS_WVALID,
            if_read           => local_HLS_WREADY,
            if_dout           => local_HLS_WDATA_PACK,
            if_num_data_valid => open);
        
        wreq_ready       <= '1' when offset_full_n = '1' or offset_write = '0' else '0';
        align_len        <= RESIZE(SHIFT_RIGHT(tmp_len + tmp_addr(BUS_ADDR_ALIGN-1 downto 0), BUS_ADDR_ALIGN), BEAT_LEN_WIDTH);
        offset_write     <= tmp_valid and in_AXI_AWREADY;

        in_wdata_pack    <= in_HLS_WSTRB & in_HLS_WDATA;
        in_wdata_vld     <= in_HLS_WVALID;
        out_HLS_WREADY   <= out_wdata_rdy;
        local_AXI_WDATA_PACK <= strb_buf(BUS_DATA_BYTES-1 downto 0) & data_buf(BUS_DATA_WIDTH-1 downto 0);
        local_AXI_WVALID <= data_valid;
        local_HLS_WREADY <= '1' when first_split_pred and (offset_valid ='1') and ready_for_data else '0';

        tmp_wdata        <= RESIZE(local_HLS_WDATA_PACK(USER_DW-1 downto 0), USER_DATA_WIDTH);
        tmp_wstrb        <= RESIZE(local_HLS_WDATA_PACK(USER_DW+USER_DW/8-1 downto USER_DW), USER_DATA_BYTES);

        next_offset      <= '1' when (beat_len_cnt = beat_len) and (offset_valid = '1') and last_split else '0';
        ready_for_data   <= data_valid = '0' or local_AXI_WREADY = '1';

        first_split      <= local_HLS_WVALID = '1' and local_HLS_WREADY ='1';
        next_split       <= not first_split_pred and ready_for_data;
        last_split       <= (split_cnt = (TOTAL_SPLIT - 1)) and ready_for_data;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    first_split_pred <= true;
                    split_cnt <= (others => '0');
                elsif ACLK_EN = '1' then
                    if last_split then
                        first_split_pred <= true;
                        split_cnt <= (others => '0');
                    elsif first_split or next_split then
                        first_split_pred <= false;
                        split_cnt <= split_cnt + 1;
                    end if;
                end if;
            end if;
        end process ;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    beat_len_cnt <= (others => '0');
                elsif ACLK_EN = '1' then
                    if next_offset = '1' then
                        beat_len_cnt <= (others => '0');
                    elsif first_split or next_split then
                        beat_len_cnt <= beat_len_cnt + 1;
                    end if;
                end if;
            end if;
        end process ;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_buf <= (others => '0');
                elsif ACLK_EN = '1' then
                    if first_split then
                        data_buf <= tmp_wdata;
                    elsif next_split then
                        data_buf <= SHIFT_RIGHT(data_buf, BUS_DATA_WIDTH);
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    strb_buf <= (others => '0');
                elsif ACLK_EN = '1' then
                    if first_split then
                        strb_buf <= tmp_wstrb;
                    elsif next_split then
                        strb_buf <= SHIFT_RIGHT(strb_buf, BUS_DATA_BYTES);
                    end if;
                end if;
            end if;
        end process;

        process (ACLK)
        begin
            if (ACLK'event and ACLK = '1') then
                if (ARESET = '1') then
                    data_valid <= '0';
                elsif ACLK_EN = '1' then
                    if first_split then
                        data_valid <= '1';
                    elsif not next_split and ready_for_data then
                        data_valid <= '0';
                    end if;
                end if;
            end if;
        end process;

    end generate bus_narrow_gen;

    -- ===================================================================
    -- generate response for all request (including request with invalid length)
    fifo_wrsp : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        DATA_WIDTH        => 1,
        ADDR_WIDTH        => log2(NUM_WRITE_OUTSTANDING),
        DEPTH             => NUM_WRITE_OUTSTANDING)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => wrsp_ready,
        if_write          => next_wreq,
        if_din            => in_wrsp_type,
        if_empty_n        => wrsp_valid,
        if_read           => wrsp_read,
        if_dout           => wrsp_type,  -- "1" for valid length request, "0" for invalid length request
        if_num_data_valid => open);
    
    user_resp : pointpillars_cnn_gmem_m_axi_fifo
    generic map (
        DATA_WIDTH        => 1,
        ADDR_WIDTH        => log2(USER_MAXREQS),
        DEPTH             => USER_MAXREQS)
    port map (
        clk               => ACLK,
        reset             => ARESET,
        clk_en            => ACLK_EN,
        if_full_n         => ursp_ready,
        if_write          => ursp_write,
        if_din            => "0",
        if_empty_n        => out_HLS_BVALID,
        if_read           => in_HLS_BREADY,
        if_dout           => open,
        if_num_data_valid => open);


    out_AXI_BREADY <= wrsp_type(0) and ursp_ready;

    in_wrsp_type   <= "1" when valid_length = '1' else "0";
    ursp_write     <= wrsp_valid and (not wrsp_type(0) or in_AXI_BVALID);
    wrsp_read      <= ursp_ready and ursp_write;

end architecture behave;



